$(document).ready(function(){const n=document.querySelector(".ap-counters"),t=new IntersectionObserver((n,t)=>{n.forEach(n=>{n.isIntersecting&&($(".counter").each(function(){const n=$(this),t=parseInt(n.text(),10);n.text(0);jQuery({Counter:0}).animate({Counter:t},{duration:1e3,easing:"swing",step:function(){n.text(Math.ceil(this.Counter))}})}),t.unobserve(n.target))})},{threshold:.5});n&&t.observe(n)});